using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using OpenDentBusiness;
using UnitTestsCore;

namespace UnitTests.SignatureBoxWrapper_Tests {
	[TestClass]
	public class SignatureBoxWrapperTests:TestBase {

		///<summary>This method will execute only once, just before any tests in this class run.</summary>
		[ClassInitialize]
		public static void SetupClass(TestContext testContext) {
		}

		///<summary>This method will execute just before each test in this class.</summary>
		[TestInitialize]
		public void SetupTest() {
		}

		///<summary>This method will execute after each test in this class.</summary>
		[TestCleanup]
		public void TearDownTest() {
		}

		///<summary>This method will execute only once, just after all tests in this class have run.</summary>
		[ClassCleanup]
		public static void TearDownClass() {
		}

		[TestMethod]
		[Documentation.Numbering(Documentation.EnumTestNum.SignatureBoxWrapper_FillSignature_GetNumberOfTabletPoints)]
		public void SignatureBoxWrapper_FillSignature_GetNumberOfTabletPoints() {
			//Old unit tests designed for non-Topaz signature boxes.
			string suffix = "52";
			Patient pat = PatientT.CreatePatient(suffix);
			List<int> listFailedTests = new List<int>();
			OpenDental.UI.SignatureBoxWrapper sbw = new OpenDental.UI.SignatureBoxWrapper();
			//1: Normal signature with \r\n
			sbw.FillSignature(false,"15.4 Normal\r\n15.4 Normal\r\n15.4 Normal3","");
			if(sbw.GetNumberOfTabletPoints(false)==0) {
				listFailedTests.Add(1);
			}
			//2: Normal signature, middle tier with \r\n
			sbw.FillSignature(false,"15.4 Normal MiddleTier\r\n15.4 Normal MiddleTier\r\n15.4 Normal MiddleTier3","");
			if(sbw.GetNumberOfTabletPoints(false)==0) {
				listFailedTests.Add(2);
			}
			//5: Normal signature with \n
			sbw.FillSignature(false,"15.4 Normal\n15.4 Normal\n15.4 Normal3","");
			if(sbw.GetNumberOfTabletPoints(false)==0) {
				listFailedTests.Add(5);
			}
			//6: Normal signature, middle tier with \n
			sbw.FillSignature(false,"15.4 Normal MiddleTier\n15.4 Normal MiddleTier\n15.4 Normal MiddleTier3","");
			if(sbw.GetNumberOfTabletPoints(false)==0) {
				listFailedTests.Add(6);
			}
			//9:  Invalid Signature (wrong key)
			sbw.FillSignature(false,"15.4 Normal\r\n15.4 Normal\r\n15.4 Normal4","");
			if(sbw.GetNumberOfTabletPoints(false)!=0) {//This test is meant to be invalid intentionally.
				listFailedTests.Add(9);
			}
			Assert.AreEqual(0,listFailedTests.Count);
		}

		[TestMethod]
		public void SignatureBoxWrapper_FillSignature_GetNumberOfTabletPointsTopaz() {
			//Old unit tests designed for Topaz signature boxes.
			string suffix = "52";
			Patient pat = PatientT.CreatePatient(suffix);
			List<int> listFailedTests = new List<int>();
			OpenDental.UI.SignatureBoxWrapper sbw = new OpenDental.UI.SignatureBoxWrapper();
			if(!sbw.CheckTopaz()) {
				Assert.Inconclusive("The Topaz SignatureBoxWrapper control could not be initialized. Try running unit tests in 32-bit mode.");
			}
			//3: Topaz signature with \r\n
			sbw.FillSignature(true,"15.4 Topaz\r\n15.4 Topaz\r\n15.4 Topaz3","");
			if(sbw.GetNumberOfTabletPoints(true)==0) {
				listFailedTests.Add(3);
			}
			//4: Topaz signature, middle tier with \r\n
			sbw.FillSignature(true,"15.4 Topaz MiddleTier\r\n15.4 Topaz MiddleTier\r\n15.4 Topaz MiddleTier3","");
			if(sbw.GetNumberOfTabletPoints(true)==0) {
				listFailedTests.Add(4);
			}
			//7: Topaz signature with \n
			sbw.FillSignature(true,"15.4 Topaz\n15.4 Topaz\n15.4 Topaz3","");
			if(sbw.GetNumberOfTabletPoints(true)==0) {
				listFailedTests.Add(7);
			}
			//8: Topaz signature, middle tier with \n
			sbw.FillSignature(true,"15.4 Topaz MiddleTier\n15.4 Topaz MiddleTier\n15.4 Topaz MiddleTier3","");
			if(sbw.GetNumberOfTabletPoints(true)==0) {
				listFailedTests.Add(8);
			}
			Assert.AreEqual(0,listFailedTests.Count);
		}

	}
}
